import { useState, useEffect } from 'react'
// import Router from 'next/router'
import { gql } from 'graphql-request'
import { useForm } from 'react-hook-form'
import { graphQLClient } from '@/utils/graphql-client'

const EditUser = ({ defaultValues, setData, id, token }: { defaultValues: any, setData: any, id: any, token: any }) => {
	const [errorMessage, setErrorMessage] = useState('')
	const [updateMessage, setUpdateMessage] = useState('')
	// const fetcher = async (query) => await graphQLClient(token).request(query)
	// const { data, error } = useSWR('/api/user', fetcher)

	const { handleSubmit, register, reset, errors } = useForm({
		defaultValues: {
			...defaultValues,
		},
	})

	const onSubmit = handleSubmit(async ({ email, favTeam }) => {
		if (errorMessage) setErrorMessage('')

		const query = gql`
      mutation UpdateAUser($id: ID!, $email: String!, $favTeam: String) {
        updateUser(id: $id, data: { email: $email, favTeam: $favTeam}) {
          email
          favTeam
        }
      }
    `

		const variables = {
			id,
			email,
			favTeam,
		}

		console.log(query)
		try {
			await graphQLClient(token).request(query, variables)
			setUpdateMessage(() => 'Profile updated')
			setData(() => variables)
		} catch (error) {
			console.error(error)
			setErrorMessage(error.message)
		}
	}) 

	useEffect(() => {
		reset(defaultValues)
	}, [reset, defaultValues])

	return (
		<>
			<form onSubmit={onSubmit} className="w-full">
				<div className="flex items-center justify-between w-full mb-2">
					<label>Task</label>
					<input
						type="text"
						name="email"
						className="p-2 ml-2 text-right border-2 border-blue-100 rounded-md shadow-md" 
						ref={register({ required: 'Email is required' })}
					/>
					{errors.email && (
						<span role="alert">
							{errors.email.message}
						</span>
					)}
				</div>

				<div className="flex items-center justify-between w-full mb-2">
					<label>Favorite Team</label>
					<input
						type="text"
						name="favTeam" 
						className="p-2 ml-2 text-right border-2 border-blue-100 rounded-md shadow-md"
						ref={register} 
					/>
					{errors.favTeam && (
						<span role="alert">
							{errors.favTeam.message}
						</span>
					)}
				</div>

				<div className="flex justify-center mt-4">
					<button type="submit" className="px-4 py-2 text-white rounded-md bg-gradient-to-tr from-blue-700 to-blue-400">Update</button>
				</div>
			</form>

			{updateMessage && (
				<p className="mt-4 text-center bg-gray-100">
					{updateMessage}
				</p>
			)}
			{errorMessage && (
				<p role="alert">
					{errorMessage}
				</p>
			)}
		</>
	)
}

export default EditUser
