import { useState, useEffect } from 'react'
import Router from 'next/router'
import { gql } from 'graphql-request'
import { useForm } from 'react-hook-form'
import { graphQLClient } from '@/utils/graphql-client'

const EditUser = ({ defaultValues, id, token }: { defaultValues: any, id: any, token: any }) => {
	const [errorMessage, setErrorMessage] = useState('')
	// const fetcher = async (query) => await graphQLClient(token).request(query)
	// const { data, error } = useSWR('/api/user', fetcher)

	const { handleSubmit, register, reset, errors } = useForm({
		defaultValues: {
			...defaultValues,
		},
	})

	const onSubmit = handleSubmit(async ({ email, favTeam }) => {
		if (errorMessage) setErrorMessage('')

		const query = gql`
      mutation UpdateAUser($id: ID!, $email: String!, $favTeam: String) {
        updateUser(id: $id, data: { email: $email, favTeam: $favTeam}) {
          email
          favTeam
        }
      }
    `

		const variables = {
			id,
			email,
			favTeam,
		}

		try {
			await graphQLClient(token).request(query, variables)
			Router.push('/profile')
		} catch (error) {
			console.error(error)
			setErrorMessage(error.message)
		}
	}) 

	useEffect(() => {
		reset(defaultValues)
	}, [reset, defaultValues])

	return (
		<>
			<form onSubmit={onSubmit}>
				<div>
					<label>Task</label>
					<input
						type="text"
						name="email"
						ref={register({ required: 'Email is required' })}
					/>
					{errors.email && (
						<span role="alert">
							{errors.email.message}
						</span>
					)}
				</div>

				<div>
					<label>Favorite Team</label>
					<input type="text" name="favTeam" ref={register()} />
					{errors.favTeam && (
						<span role="alert">
							{errors.favTeam.message}
						</span>
					)}
				</div>

				<div>
					<button type="submit">Update</button>
				</div>
			</form>

			{errorMessage && (
				<p role="alert">
					{errorMessage}
				</p>
			)}
		</>
	)
}

export default EditUser
