import Layout from '@/components/layout'
import { getAuthCookie } from '@/utils/auth-cookies'
import { graphQLClient } from '@/utils/graphql-client'
import { gql } from 'graphql-request'
import useSWR from 'swr'

const Profile = ({ token, user }: {token: any, user: any}) => {
	const { id } = user

	const query = gql`
		query FindCurrentUser($id: ID!) {
			findUserByID(id: $id) {
				email
				todos {
					data {
						task
						completed
					}
				}
			}    
		}
	`

	const fetcher = async (query) => await graphQLClient(token).request(query, { id })

	const { data, error } = useSWR([query, id], fetcher)
	console.log(data)

	if (error) return (
		<Layout>
			<div>Error...</div>
		</Layout>
	)
	return (
		<Layout>
			{JSON.stringify(data)}
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = await getAuthCookie(ctx.req)
	const user = await useSWR('/api/user')
	return { props: { token: token || null, user } }
}

export default Profile
