import Image from 'next/image'
import Layout from '@/components/layout'

const TeamPicker = ({ teams }: { teams: any}) => {

	return (
		<Layout>
			<div className="flex flex-wrap justify-between max-w-6xl text-sm">
				{teams.map((team) => (
					<div key={team.idTeam} className="w-1/4 ">
						<div className="flex items-center w-full p-4 mx-2 my-2 bg-blue-100 rounded-md ">
							<Image src={team.strTeamBadge} width={30} height={30} />
							<span className="ml-4 text-lg">
								{team.strTeam}
							</span>
						</div>
					</div>
				))}
			</div>
		</Layout>
	)
}

export async function getStaticProps() {

	const { teams } = await fetch('https://www.thesportsdb.com/api/v1/json/1/lookup_all_teams.php?id=4328').then((res) => res.json())

	return {
		props: {
			teams
		}
	}
}

export default TeamPicker
