	// 	.request(query, variables)
	let info
	console.log({info})
	// const { data, error } = useSWR([query, variables], fetcher)
	useEffect(() => {
		info = graphQLClient(token).request(query, variables)
	}, [data])

	// if (error) return (
	// 	<Layout>
	// 		<div>Error...</div>
	// 	</Layout>
	// )
	return (
		<Layout>
			{ data ? (
				<div>
					{JSON.stringify(userTodos)}
				</div>
			) : (
				<div>Loading...</div>
			)}
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = await getAuthCookie(ctx.req)
	return { 
		props: { 
			token: token || null,
		}
	}
}

export default Profile
