import { gql } from 'graphql-request'
import Layout from '../components/layout'
import { graphQLClient } from '../utils/graphql-client'
import { getAuthCookie } from '@/utils/auth-cookies'
import useSWR from 'swr'
import { useEffect, useState } from 'react'

const Home = ({token, upcomingGames}: { token: any, upcomingGames: any }) => {
	const [leagues, setLeagues] = useState()
	const [errorMessage, setErrorMessage] = useState('')
	const fetcher = async (query) => await graphQLClient(token).request(query)



	const getLeagues = async () => {
		const query = gql`
		{
		allLeagues {
			data {
				name
				members {
					data {
						username
						}
					}
				}
  		}
		}
    `
		try {
			const { data } = useSWR(query, fetcher)
			setLeagues(() => data)

		} catch (error) {
			console.error(error)
			setErrorMessage(() => error)
		}
	}


	if (errorMessage) return (
		<Layout>
			<div>Error...</div>
		</Layout>
	)

	useEffect(() => {
		getLeagues()
	}, [])

	return (
		<Layout>
			<div className="flex flex-col items-center w-full mx-auto">
				{leagues && (
					<div>
						{JSON.stringify(leagues)}
					</div>
				)}
				{upcomingGames ? (
					<div className="w-full sm:w-1/2">
						<p className="mb-2 font-light">Next 5 PL Games</p>
						{upcomingGames.events.slice(0, 5).map((event) => (
							<div key={event.idEvent} className="flex items-center justify-between w-full mb-1">
								<div className="w-1/3">
									{event.strHomeTeam}
								</div>
								<div className="w-1/3">
									{event.strAwayTeam}
								</div>
								<div className="w-1/3 text-sm">
									{event.dateEvent}
								</div>
							</div>
						))}
					</div>
				): (
					<div>Loading...</div>
				)}

			</div>
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = getAuthCookie(ctx.req)
	const upcomingGames = await fetch('https://www.thesportsdb.com/api/v1/json/1/eventsnextleague.php?id=4328').then(res => res.json())
	return { props: { 
		token: token || null,
		upcomingGames
	} }
}

export default Home
