import useSWR from 'swr'
import Link from 'next/link'
import { gql } from 'graphql-request'
import Layout from '../components/layout'
import { graphQLClient } from '../utils/graphql-client'
import { getAuthCookie } from '@/utils/auth-cookies'

const Home = ({token, upcomingGames}: { token: any, upcomingGames: any }) => {
	console.log(upcomingGames)
	const fetcher = async (query) => await graphQLClient(token).request(query)
	const { data, error, mutate } = useSWR(
		gql`
      {
        allTodos {
          data {
            _id
            task
            completed
          }
        }    
      }
    `, fetcher
	)

	if (error) return (
		<Layout>
			<div>Error...</div>
		</Layout>
	)

	const toggleTodo = async (id, completed) => {
		const query = gql`
      mutation PartialUpdateTodo($id: ID!, $completed: Boolean!) {
        partialUpdateTodo(id: $id, data: { completed: $completed }) {
          _id
          completed
        }
      }
    `

		const variables = {
			id,
			completed: !completed,
		}

		try {
			await graphQLClient(token)
				.setHeader('X-Schema-Preview', 'partial-update-mutation')
				.request(query, variables)
			mutate()
		} catch (error) {
			console.error(error)
		}
	}

	const deleteATodo = async (id) => {
		const query = gql`
      mutation DeleteATodo($id: ID!) {
        deleteTodo(id: $id) {
          _id
        }
      }
    `

		try {
			await graphQLClient(token).request(query, { id })
			mutate()
		} catch (error) {
			console.error(error)
		}
	}

	return (
		<Layout>
			<div className="flex flex-col items-center w-full mx-auto">
				<h1 className="text-3xl">Next Fauna GraphQL</h1>
				<div className="mt-2 mb-6">
					<Link href="/new">
						<a className="px-4 py-2 text-white rounded-md hover:underline hover:text-blue-700 bg-gradient-to-tr from-blue-800 to-blue-500">Create new</a>
					</Link>
				</div>

				{upcomingGames ? (
					<div>
						<p className="mb-2 font-light">Next 5 PL Games</p>
						{upcomingGames.events.slice(0, 5).map((event) => (
							<div key={event.idEvent} className="flex justify-between mb-1">
								<div className="w-1/3">
									{event.strHomeTeam}
								</div>
								<div className="w-1/3">
									{event.strAwayTeam}
								</div>
								<div className="w-1/3">
									{event.dateEvent}
								</div>
							</div>
						))}
					</div>
				): (
					<div>Loading...</div>
				)}

			</div>
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = getAuthCookie(ctx.req)
	const upcomingGames = await fetch('https://www.thesportsdb.com/api/v1/json/1/eventsnextleague.php?id=4328').then(res => res.json())
	return { props: { 
		token: token || null,
		upcomingGames
	} }
}

export default Home
