// import { useEffect, useState } from 'react'
// import { gql } from 'graphql-request'
import useSWR from 'swr'
import Layout from '@/components/layout'
import { getAuthCookie } from '@/utils/auth-cookies'
// import { graphQLClient } from '@/utils/graphql-client'
import EditUser from '@/components/edit-user'

const Profile = ({ token }: {token: any}) => {
	const fetcher = (url) => fetch(url).then((r) => r.json())
	const { data, error } = useSWR('/api/user', fetcher)
	if (error) {
		console.log(error)	
		return (
			<Layout>
				<div>Error...</div>
			</Layout>
		)
	}
	return (
		<Layout>
			<div className="flex justify-center w-full">
				<h1 className="pb-4 text-2xl">User profile</h1>
				{ data ? (
					<div className="flex flex-col mt-4">
						<div className="flex flex-wrap justify-between mb-4" key={data.id}>
							<p className="w-1/2">Email:</p>
							<p className="w-1/2">{data.email}</p>
							{data.favTeam && (
								<>
									<p className="w-1/2">Team:</p>
									<p className="w-1/2">{data.favTeam}</p>
								</>
							)} 
						</div>
						<EditUser defaultValues={data} id={data.id} token={token} />
					</div>
				) : (
					<div>Loading...</div>
				)}
			</div>
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = await getAuthCookie(ctx.req)
	return { 
		props: { 
			token: token || null,
		}
	}
}

export default Profile
