import { gql } from 'graphql-request'
import Layout from '../components/layout'
import { graphQLClient } from '../utils/graphql-client'
import { getAuthCookie } from '@/utils/auth-cookies'
import useSWR from 'swr'
import { useEffect, useState } from 'react'

const Home = ({token, upcomingGames}: { token: any, upcomingGames: any }) => {
	const [leagues, setLeagues] = useState()
	const [errorMessage, setErrorMessage] = useState('')
	// const fetcher = async (query) => await graphQLClient(token).request(query)

	const getLeagues = async() => {
		const query = gql`
		query MyQuery {
			allLeagues {
				data {
					_id
					name
					members {
						data {
							_id
							username
						}
					}
					}
				}
		}`
		try {
			await graphQLClient(token).request(query).then((res) => setLeagues(() => res))
			// setLeagues(() => data)
		} catch (error) {
			console.error(error)
			setErrorMessage(error.message)
		}

	}
	// const { data, error } = useSWR(query, fetcher)
	useEffect(() => {
		getLeagues()
		console.log(leagues)
	}, [])

	// if (error) {
	// 	console.error(error)
	// 	setErrorMessage(() => error)
	// }


	if (errorMessage) return (
		<Layout>
			<div>Error...</div>
		</Layout>
	)

	return (
		<Layout>
			<div className="flex flex-col items-center w-full mx-auto">
				{leagues && (
					<div>
						<p>Leagues</p>
						{leagues.allLeagues.map((league) => (
							<div key={league._id}>
								<div>
									{league.name}
								</div>
								{league.members && (
									league.members.data.map((member) => (
										<div key={member._id}>
											{member.username}
										</div>
									))
								)}
							</div>
						))}
					</div>
				)}
				{upcomingGames ? (
					<div className="w-full sm:w-1/2">
						<p className="mb-2 font-light">Next 5 PL Games</p>
						{upcomingGames.events.slice(0, 5).map((event) => (
							<div key={event.idEvent} className="flex items-center justify-between w-full mb-1">
								<div className="w-1/3">
									{event.strHomeTeam}
								</div>
								<div className="w-1/3">
									{event.strAwayTeam}
								</div>
								<div className="w-1/3 text-sm">
									{event.dateEvent}
								</div>
							</div>
						))}
					</div>
				): (
					<div>Loading...</div>
				)}

			</div>
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = getAuthCookie(ctx.req)
	const upcomingGames = await fetch('https://www.thesportsdb.com/api/v1/json/1/eventsnextleague.php?id=4328').then(res => res.json())
	return { props: { 
		token: token || null,
		upcomingGames
	} }
}

export default Home
