import useSWR from 'swr'
import Link from 'next/link'
import { gql } from 'graphql-request'
import Layout from '../components/layout'
import { graphQLClient } from '../utils/graphql-client'

const fetcher = async (query) => await graphQLClient.request(query)

const Home = ({token}: { token: any }) => {
	const { data, error, mutate } = useSWR(
		gql`
      {
        allTodos {
          data {
            _id
            task
            completed
          }
        }    
      }
    `, fetcher
	)

	if (error) return <div>Error...</div>

	const toggleTodo = async (id, completed) => {
		const query = gql`
      mutation PartialUpdateTodo($id: ID!, $completed: Boolean!) {
        partialUpdateTodo(id: $id, data: { completed: $completed }) {
          _id
          completed
        }
      }
    `

		const variables = {
			id,
			completed: !completed,
		}

		try {
			await graphQLClient.request(query, variables)
			mutate()
		} catch (error) {
			console.error(error)
		}
	}

	const deleteATodo = async (id) => {
		const query = gql`
      mutation DeleteATodo($id: ID!) {
        deleteTodo(id: $id) {
          _id
        }
      }
    `

		try {
			await graphQLClient(token).request(query, { id })
			mutate()
		} catch (error) {
			console.error(error)
		}
	}

	return (
		<Layout>
			<div className="flex flex-col w-full max-w-3xl">
				<h1 className="text-3xl">Next Fauna GraphQL</h1>
				<Link href="/new">
					<a className="w-auto hover:underline hover:text-blue-700">Create new todo</a>
				</Link>

				{data ? (
					<div>
						{data.allTodos.data.map((todo) => (
							<div key={todo._id} className="flex justify-between w-full">
								<span
									onClick={() => toggleTodo(todo._id, todo.completed)}
									className={`w-1/2 cursor-pointer hover:text-blue-700 ${todo.completed ? 'line-through' : 'no-underline'}`}
								>
									{todo.task}
								</span>
								<Link href="/todo/[id]" as={`/todo/${todo._id}`}>
									<a className="w-1/4">Edit</a>
								</Link>
								<span onClick={() => deleteATodo(todo._id)} className="w-1/4">
                Delete
								</span>
							</div>
						))}
					</div>
				): (
					<div>Loading...</div>
				)}

			</div>
		</Layout>
	)
}

export default Home
