import { useEffect, useState } from 'react'
import { gql } from 'graphql-request'
import useSWR from 'swr'
import Layout from '@/components/layout'
import { getAuthCookie } from '@/utils/auth-cookies'
import { graphQLClient } from '@/utils/graphql-client'

const Profile = ({ token }: {token: any}) => {
	const { data } = useSWR('/api/user')
	console.log(data)
	const [userTodos, setUserTodos] = useState([])
	const query = gql`
		query FindCurrentUser($id: ID!) {
			findUserByID(id: $id) {
				email
				todos {
					data {
						task
						completed
					}
				}
			}    
		}
	`

	// const variables = {
	// 	id: user && user.id
	// }

	// const fetcher = async (query) => await graphQLClient(token).request(query, variables)
	// const info = useSWR([query, variables], fetcher)
	// console.log(info)
	// const { data, error } = useSWR([query, variables], fetcher)
	console.log(data)
	useEffect(() => {
		setUserTodos(data)
	}, [data])

	// if (error) return (
	// 	<Layout>
	// 		<div>Error...</div>
	// 	</Layout>
	// )
	return (
		<Layout>
			{ data ? (
				<div>
					{JSON.stringify(userTodos)}
				</div>
			) : (
				<div>Loading...</div>
			)}
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = await getAuthCookie(ctx.req)
	return { props: { token: token || null } }
}

export default Profile
