import { useEffect, useState } from 'react'
import { gql } from 'graphql-request'
import useSWR from 'swr'
import Layout from '@/components/layout'
import { getAuthCookie } from '@/utils/auth-cookies'
import { graphQLClient } from '@/utils/graphql-client'

const Profile = ({ token }: {token: any}) => {
	let user
	const { data = user } = useSWR('/api/user')
	// console.log(data)
	const [userTodos, setUserTodos] = useState()
	const query = gql`
	query MyQuery($id: ID!)
	{
			findUserByID(id: $id) {
				email
				todos {
					data {
						task
						completed
					}
				}
			}    
		}
	`

	const variables = {
		id: user && user.id
	}

	// const fetcher = async () => await graphQLClient(token)
	// 	.request(query, variables)
	let info
	console.log({info})
	// const { data, error } = useSWR([query, variables], fetcher)
	useEffect(() => {
		if (data) {
			info = graphQLClient(token).request(query, variables)
		}
	}, [data])

	// if (error) return (
	// 	<Layout>
	// 		<div>Error...</div>
	// 	</Layout>
	// )
	return (
		<Layout>
			{ data ? (
				<div>
					{JSON.stringify(userTodos)}
				</div>
			) : (
				<div>Loading...</div>
			)}
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = await getAuthCookie(ctx.req)
	return { 
		props: { 
			token: token || null,
		}
	}
}

export default Profile
