// import { useRouter } from 'next/router'
import { gql } from 'graphql-request'
import Layout from '@/components/layout'
import { graphQLClient } from '@/utils/graphql-client'
import { getAuthCookie } from '@/utils/auth-cookies'
import TeamPicker from '@/components/teampicker'
import { useState } from 'react'

const League = ({ data, teams, token }: { data: any, teams: any, token: any }) => {
	const [pickedTeam, setPickedTeam] = useState(data.options ? data.options.teams : [])
	const [options, setOptions] = useState(data.options ? data.options : {})
	const [upcomingEvents, setUpcomingEvents] = useState(null)

	const getEvents = async (teamId) => {
		const { events } = await fetch(`https://www.thesportsdb.com/api/v1/json/1/eventsnext.php?id=${teamId}`).then((res) => res.json())
		setUpcomingEvents((prev) => ({...prev, [teamId]: events}))
		console.log(upcomingEvents)
	}
	const [errorMessage, setErrorMessage] = useState('')
	const [updateMessage, setUpdateMessage] = useState('')
	const updateTeam = async () => {
		const query = gql`
      mutation UpdateLeague($id: ID!, $teams: [String], $public: Boolean, $class: LeagueType) {
        updateLeagueOptions(id: $id, data: { teams: $teams, public: $public, class: $class }) {
          teams
					public
					class
        }
      }
    `

		const variables = {
			id: data.options._id,
			teams: pickedTeam,
			public: options.public,
			class: options.class
		}

		try {
			await graphQLClient(token).request(query, variables)
			setTimeout(() => {
				setUpdateMessage('')
			}, 4000)
			setUpdateMessage('Changes saved')
		} catch (error) {
			console.error(error)
			setErrorMessage(error.message)
		}
	}

	return (
		<Layout>
			<h1>League info</h1>
			<div className="flex w-full">
				<div className="w-1/2">
					{pickedTeam.map((team) => {
						getEvents(team)
						// return (
						// 	JSON.stringify(upcomingEvents[team])
						// )
					}
					)}
				</div>
				{data ? (
					<div className="w-1/2">
						<h2>
						League name: {data.name}
						</h2>
						<div>
						League members: 
							{data.members.data.length > 0 ? (
								data.members.data.map((item) => (
									<div key={item.username}>
										{item.username} ({item.email})
									</div>
								))
							) : (
								<span className="ml-2 text-sm italics">
								No members :(
								</span>
							)}
						</div>
						<div>
						League options:
							<select value={options.class} onChange={(e) => setOptions((prev) => ({...prev, class: e.target.value}))}>
								{['SINGLETEAM',	'SINGLEDIVISION', 'MULTITEAM', 'MULTIDIVISION', 'ANY'].map((item) => (<option key={item}>{item}</option>))}
							</select>
							{data.options ? (
								<div>
									<div className="flex flex-col items-start justify-start text-sm">
										<button
											className={`${options.public && 'bg-blue-300'} w-20 p-2 rounded-md`}
											onClick={() => setOptions((prev) => ({...prev, public: true }))}
										>
										Public
										</button>
										<button
											className={`${!options.public && 'bg-blue-300'}  w-20 p-2 rounded-md`}
											onClick={() => setOptions((prev) => ({...prev, public: false }))}
										>
										Private
										</button>
									</div>
								</div>
							) : (
								<div className="ml-2 text-sm italics">
								No options set
								</div>
							)}
						</div>

					</div>
				): (
					<div>Loading...</div>
				)}
				<div>

				</div>
			</div>
			<button onClick={() => updateTeam()}>Click to save changes</button>

			{updateMessage && (
				<p>{updateMessage}</p>
			)}
			{errorMessage && (
				<p>{errorMessage}</p>
			)}
			<TeamPicker teams={teams} picker={[pickedTeam, setPickedTeam]} />

		</Layout>
	)
}


export async function getServerSideProps(ctx: any) {
	const { id } = ctx.params
	const token = getAuthCookie(ctx.req)

	const { teams } = await fetch('https://www.thesportsdb.com/api/v1/json/1/lookup_all_teams.php?id=4328').then((res) => res.json())

	const query = gql`
		query FindLeague($id: ID!) {
			findLeagueByID(id: $id) {
				name
				_id
				options {
					_id
					class
					public
					teams
					divisions
				}
				members {
					data {
						_id
						username
						email
					}
				}
			}
		}`
	const res = await graphQLClient(token).request(query, {id})
	console.log(res)
	const data = await res

	return { props: { 
		token: token || null,
		data: data?.findLeagueByID,
		teams
	} }
}

export default League
