import { useState } from 'react'
import { gql } from 'graphql-request'
import useSWR from 'swr'
import Layout from '@/components/layout'
import { getAuthCookie } from '@/utils/auth-cookies'
import { graphQLClient } from '@/utils/graphql-client'

const Profile = ({ token }: {token: any}) => {
	const { data: user } = useSWR('/api/user')
	const [userTodos, setUserTodos] = useState([])
	const query = gql`
		query FindCurrentUser($id: ID!) {
			findUserByID(id: $id) {
				email
				todos {
					data {
						task
						completed
					}
				}
			}    
		}
	`

	const fetcher = async (query) => await graphQLClient(token).request(query, { user })

	const { data, error } = useSWR([query, user.id], fetcher)
	console.log(data)

	if (error) return (
		<Layout>
			<div>Error...</div>
		</Layout>
	)
	return (
		<Layout>
			{JSON.stringify(data)}
		</Layout>
	)
}

export async function getServerSideProps(ctx: any) {
	const token = await getAuthCookie(ctx.req)
	return { props: { token: token || null } }
}

export default Profile
