// import { useRouter } from 'next/router'
import { gql } from 'graphql-request'
import Layout from '@/components/layout'
import { graphQLClient } from '@/utils/graphql-client'
import { getAuthCookie } from '@/utils/auth-cookies'
import TeamPicker from 'pages/teampicker'
import { useState } from 'react'

const League = ({ data, teams }: { data: any, teams: any }) => {
	const [pickedTeam, setPickedTeam] = useState(data.options.teams)
	return (
		<Layout>
			<h1>League info</h1>

			{data ? (
				<div>
					<h2>
						League name: {data.name}
					</h2>
					<div>
						League members: 
						{data.members.data.length > 0 ? (
							data.members.data.map((item) => (
								<div key={item.username}>
									{item.username} ({item.email})
								</div>
							))
						) : (
							<span className="ml-2 text-sm italics">
								No members :(
							</span>
						)}
					</div>
					<div>
						League options:
						{data.options ? (
							<div>
								<span className="mr-4 text-sm">
									{data.options.class}
								</span>
								<span className="text-sm">
									{data.options.public ? 'Public' : 'Private'}
								</span>
								{JSON.stringify(data.options)}
							</div>
						) : (
							<div className="ml-2 text-sm italics">
								No options set
							</div>
						)}
					</div>

				</div>
			): (
				<div>Loading...</div>
			)}
			{teams !== pickedTeam && (
				<button>Click to save changes</button>
			)}
			<TeamPicker teams={teams} picker={[pickedTeam, setPickedTeam]} />
		</Layout>
	)
}


export async function getServerSideProps(ctx: any) {
	const { id } = ctx.params
	const token = getAuthCookie(ctx.req)

	const { teams } = await fetch('https://www.thesportsdb.com/api/v1/json/1/lookup_all_teams.php?id=4328').then((res) => res.json())

	const query = gql`
		query FindLeague($id: ID!) {
			findLeagueByID(id: $id) {
				name
				options {
					class
					public
					teams
					divisions
				}
				members {
					data {
						_id
						username
						email
					}
				}
			}
		}`
	const res = await graphQLClient(token).request(query, {id})
	console.log(res)
	const data = await res

	return { props: { 
		data: data?.findLeagueByID,
		teams
	} }
}

export default League
