import Image from 'next/image'
import Layout from '@/components/layout'

const TeamPicker = ({ teams, pickedTeam, setPickedTeam }: { teams: any, pickedTeam: any, setPickedTeam: any}) => {

	return (
		<div className="flex flex-wrap justify-between max-w-6xl text-sm">
			{teams.map((team) => (
				<div key={team.idTeam} className="w-1/4 px-2">
					<div className={`flex items-center w-full p-4 mx-2 my-2 rounded-md bg-blue-50 ${pickedTeam === team.idTeam && 'bg-blue-200'}`}>
						<Image src={team.strTeamBadge} width={30} height={30} />
						<span className="ml-4 text-lg">
							{team.strTeam}
						</span>
					</div>
				</div>
			))}
		</div>
	)
}

export default TeamPicker
